import { useState } from 'react'

const App = () => {
  const [persons, setPersons] = useState([
    { name: 'Arto Hellas', num: '040-123456', id: 1 },
    { name: 'Ada Lovelace', num: '39-44-5323523', id: 2 },
    { name: 'Dan Abramov', num: '12-43-234345', id: 3 },
    { name: 'Mary Poppendieck', num: '39-23-6423122', id: 4 }
  ])
  const [newName, setNewName] = useState('')
  const [newNum, setNewNum] = useState('')
  const [filterName, setFilterName] = useState('')

  const lowerCase = persons.map((persons) => persons.name.toLowerCase())
  const personsToShow = lowerCase.filter(lowerCase => lowerCase.includes(filterName.toLowerCase()))
  console.log(lowerCase)
  console.log(personsToShow)
  const addNote = (event) =>{
    event.preventDefault()
    const NameObject = {
      name: newName,
      num: newNum,
      id: persons.length + 1
    }
    setPersons(persons.concat(NameObject))
    setNewName('')
    setNewNum('')
    setFilterName('')
  }
  const handPersonChange = (event) => {
    setNewName(event.target.value)
  }
  const handNumberChange = (event) => {
    setNewNum(event.target.value)
  }
  const handFilterNameChange = (event) => {
    setFilterName(event.target.value) 
  }
  return (
    <div>
      <h2>Phonebook</h2>
      <form>
        <div>
          filter shown with <input
          onChange = {handFilterNameChange}
          />
        </div>
      </form>
      <h2>add a new</h2>
      <form onSubmit = {addNote}>
        <div>
          name: <input 
          onChange = {handPersonChange}
          />
        </div>
        <div> 
          number: <input
          onChange = {handNumberChange}
          />
        </div>  
        <div>
          <button type = "submit"> add </button>
        </div>
      </form>
      <h2>Numbers</h2>
      <p>
        {
          personsToShow.map(persons => <p key = {persons.id} > {persons.name} {persons.num}</p>)
        }
      </p>
    </div>
  )
}

export default App
