import { useState } from 'react'

const App = () => {
  const [persons, setPersons] = useState([
    { name: 'Arto Hellas' }
  ]) 
  const [newName, setNewName] = useState('')
  const name = []
  function Check(x){
    for(var i = 0; i < name.length; i++)
      if(JSON.stringify(name[i]) === JSON.stringify(x))
        return false
    return true
  }
  const addNote = (event) =>{
    event.preventDefault()
    const NameObject = {
      name: newName,
    }
    console.log(NameObject.name)
    console.log(name[NameObject.name])

    if(Check(NameObject.name) == true){
      setPersons(persons.concat(NameObject))
      name.push(NameObject)
    }
    else 
      alert("hi")

    setNewName('')
  }
  const handPersonChange = (event) => {
    setNewName(event.target.value)
  }
  return (
    <div>
      <h2>Phonebook</h2>
      <form onSubmit = {addNote}>
        <div>
          name: <input 
          onChange = {handPersonChange}
          />
        </div>
        <div>
          <button type = "submit"> add </button>
        </div>
      </form>
      <h2>Numbers</h2>
      <p>
        {
          persons.map(persons => <p> {persons.name} </p>)
        }
      </p>
    </div>
  )
}

export default App
