import { useState } from 'react'

const Button = ({label, value}) => {
  return (
    <button onClick = {label}> {value} </button>
  )
};
const StatisticLine = ({label, value}) => {
  return (
    <p> {label} {value} </p>
  )
};

// a proper place to define a component
const Statistics = (props) => {
  var total = props.good + props.neutral + props.bad
  var total_point = props.good - props.bad
  var positive_num = props.good / total * 100

  if(total == 0)
    return <StatisticLine label = "No feedback given"/>
  else if(total > 0)
    return (
      <div>
      <p> good {props.good} </p>
      <p> neutral {props.neutral} </p>
      <p> bad {props.bad} </p>
      <p> all {props.good + props.neutral + props.bad} </p>
      <p> average {total_point / total} </p>
      <p> positive {props.good / total * 100} % </p>
      </div>
    )
}

const App = () => {

  const [good, setGood] = useState(0)
  const [neutral, setNeutral] = useState(0)
  const [bad, setBad] = useState(0)
  const increaseGoodByOne = () => setGood(good + 1)
  const increaseNeutralByOne = () => setNeutral(neutral + 1)
  const increaseBadByOne = () => setBad(bad + 1)
  const Feedback = () => <Statistics good = {good} bad = {bad} neutral = {neutral}/>
  return (
    <div>
      <h1> give feedback </h1>
      <Button label = {increaseGoodByOne} value = "good"/>
      <Button label = {increaseNeutralByOne} value = "neutral"/>
      <Button label = {increaseBadByOne} value = "bad"/>
      <h1> statistics </h1>
      <Statistics good ={good} neutral = {neutral} bad = {bad}/>
    </div>
  )
}

export default App
