import { useState } from 'react'

// a proper place to define a component
const Statistics = (props) => {
  const [good, setGood] = useState(0)
  const [neutral, setNeutral] = useState(0)
  const [bad, setBad] = useState(0)
  const increaseGoodByOne = () => setGood(props.good + 1)
  const increaseNeutralByOne = () => setNeutral(props.neutral + 1)
  const increaseBadByOne = () => setBad(props.bad + 1)
  var total = props.good + props.neutral + props.bad
  var total_point = props.good - props.bad
}

const App = () => {

  const [good, setGood] = useState(0)
  const [neutral, setNeutral] = useState(0)
  const [bad, setBad] = useState(0)
  // do not define a component within another component
  const Statistics = (props) => {
    props.good = good;
    props.neutral = neutral;
    props.bad = bad; 
  }

  return (
    <div>
    <h1> give feedback </h1>

    <Statistics />
    </div>
  )
}

export default App
