import { useState } from 'react'

// a proper place to define a component
const Statistics = (props) => {
  var total = props.good + props.neutral + props.bad
  var total_point = props.good - props.bad
  var positive_num = props.good / total * 100
  return (
    <div>
    <p> good {good} </p>
    <p> neutral {neutral} </p>
    <p> bad {bad} </p>
    <p> all {good + neutral + bad} </p>
    <p> average {total_point / total} </p>
    <p> positive {good / total * 100} % </p>
    </div>
  )
}

const App = () => {

  const [good, setGood] = useState(0)
  const [neutral, setNeutral] = useState(0)
  const [bad, setBad] = useState(0)
  // do not define a component within another component
  const Statistics = (props) => {
    const increaseGoodByOne = () => props.setGood(props.good + 1)
    const increaseNeutralByOne = () => props.setNeutral(props.neutral + 1)
    const increaseBadByOne = () => props.setBad(props.bad + 1)
    return (
      <div>
        <button onclick = {increaseGoodByOne}> good </button>
        <button onclick = {increaseNeutralByOne}> netral </button>
        <button onclick = {increaseBadByOne}> bad </button>
      </div>
    )
  }

  return (
    <div>
    <h1> give feedback </h1>
    <Statistics good = {good} bad = {bad} neutral = {neutral}/>
    <h1> statistics </h1>

    </div>
  )
}

export default App
